import React, { useState, useEffect } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";

function LoadingButton(props) {
  const setLoading = props.setLoading;
  const loading = props.loading;

  useEffect(() => {
    if (props.loading) {
      props.onSearchSubmitted(props.value).then(() => {
        setLoading(false);
      });
    }
  }, [isLoading]);

  const handleClick = () => setLoading(true);

  return (
    <Button
      variant="primary"
      disabled={isLoading}
      onClick={!isLoading ? handleClick : null}
    >
      {isLoading ? "Loading…" : "Click to load"}
    </Button>
  );
}

export default function SearchBar(props) {
  const [state, setState] = useState({
    value: "",
    loading: false,
  });
  let value = state.value;

  function setValue(inValue) {
    value = inValue;
    setState({
      value: inValue,
      loading: state.loading,
    });
  }

  function setLoading(inLoading) {
    setState({
      value: state.value,
      loading: inLoading,
    });
  }
  let loading = false;
  const handleChange = (e) => {
    if (e.key === "Enter") {
      loading = true;
      props.onSearchSubmitted(value).then(() => {
        loading = false;
      });
    }
    if (e) setValue(e.target.value);
  };

  return (
    <Form>
      <Row>
        <Col sm={10}>
          <Form.Control
            type={"text"}
            placeholder={"search"}
            value={value}
            onChange={handleChange}
          />
        </Col>
        <Col sm={2} className={"searchButtonCol"}>
          <LoadingButton
            onSearchSubmitted={props.onSearchSubmitted}
            value={value}
            loading={loading}
            setLoading={setLoading()}
          />
        </Col>
      </Row>
    </Form>
  );
}
